import { Logger } from './vendor/logger.min.mjs';

class CollectorSingleton {
	/**
	 * The version of the module.
	 */
	version = "VERSION_REPLACE_ME";
	/**
	 * The constructor of the Diob class
	 * @private
	 */
	static DiobConstructor = (() => {
		const protoDiob = VYLO.newDiob();
		const DiobConstructor = protoDiob.__proto__.constructor;
		VYLO.delDiob(protoDiob);
		return DiobConstructor;
	})();
	/**
	 * Collection limit on arrays.
	 * @private
	 * @type {number}
	 */
	collectionLimit = 20;
	/**
	 * Warning limit.
	 * @private
	 * @type {number}
	 */
	static WARNING_LIMIT = 200;
	/**
	 * A default collection to use.
	 * @private
	 * @type {Array}
	 */
	basicCollection = [];
	/**
	 * @private
	 */
	constructor() {
        /** The logger module this module uses to log errors / logs.
         * @private
         * @type {Object}
         */
        this.logger = new Logger();
        this.logger.registerType('Collector-Module', '#ff6600');
	}
	/**
	 * Sets the max collection limit of this module.
	 * @param {number} pLimit - The max limit of instances a collection can hold.
	 */
	setMaxLimit(pLimit) {
		if (typeof(pLimit) === 'number') {
			this.collectionLimit = Math.round(pLimit);
			if (this.collectionLimit > Collector.WARNING_LIMIT) {
				this.logger.prefix('Collector-Module').warn('Collector: This is a high value to use for a max limit in a collection! Only use this high of a value if you know what you are doing.')
			}
		}
	}
	/**
	 * Collects a instance into a collection.
	 * @param {Object} pCollected - The instance to collect.
	 * @param {Array} pCollection - The collection array to collect the instance to.
	 */
	collect(pCollected, pCollection) {
		const arrayCollected = Array.isArray(pCollected);
		// If there was nothing passed to be collected
		if (!pCollected) {
			this.logger.prefix('Collector-Module').error('Collector: There was nothing passed for the %cpCollected', 'font-weight: bold', 'parameter. Expecting a instance or an object.');
			return;
		}
		// If you are passing a empty object it will not be collected
		if (typeof(pCollected) === 'object' && !arrayCollected && !Object.keys(pCollected).length) {
			this.logger.prefix('Collector-Module').error('Collector: OOPS! %cpCollected', 'font-weight: bold', ' is an empty object and will NOT be collected.');
			return;
		}
		// If you are passing an object that is not a Diob or a Object, it will not be accepted. Vylocity types all have the type variable
		if (!pCollected.type && !arrayCollected) {
			this.logger.prefix('Collector-Module').error('Collector: OOPS! %cpCollected', 'font-weight: bold', ' is not a valid object It has no type.');
			return;
		}

		if (Array.isArray((pCollection))) {
			if (pCollection.includes(pCollected)) {
				this.logger.prefix('Collector-Module').error('Collector: OOPS! %cpCollected', 'font-weight: bold', 'already belongs to the provided collection.');
				return;
			}
			if (arrayCollected) {
				if (!pCollected.length) {
					this.logger.prefix('Collector-Module').error('Collector: OOPS! %cpCollected', 'font-weight: bold', 'is an array. But it contains nothing to recycle.');
					return;
				}
			}

			if (arrayCollected) {
				// If you try to collect a instance to be recycled and the collection you are recycling it to is full, it is deleted instead.
				if (pCollection.length >= this.collectionLimit) {
					for (let i = pCollected.length - 1; i >= 0; i--) {
						const instance = pCollected[i];
						if (instance instanceof CollectorSingleton.DiobConstructor) {
							this.cleanInstance(instance);
							VYLO.delDiob(instance);
						} else {
							this.cleanInstance(instance);
							VYLO.delObject(instance);
						}
						pCollected.splice(i, 1);
					}
					return;
				// If this collectedArray has more instances than the collection can handle, the access is deleted
				} else if (pCollected.length + pCollection.length > this.collectionLimit) {
					const remainder = pCollected.length - (this.collectionLimit - pCollection.length);
					for (let c = remainder; c > 0; c--) {
						const instance = pCollected[c];
						if (instance instanceof CollectorSingleton.DiobConstructor) {
							this.cleanInstance(instance);
							VYLO.delDiob(instance);
						} else {
							this.cleanInstance(instance);
							VYLO.delObject(instance);
						}
						pCollected.splice(c, 1);
					}
				}
				// The remaining instances that are in the collectedArray is now cleaned and processed and added to the collection
				for (let k = pCollected.length - 1; k >= 0; k--) {
					const instance = pCollected[k];
					if (typeof(instance.onCollected) === 'function') instance.onCollected();
					if (!pCollection.includes(instance)) pCollection.push(instance);
					this.cleanInstance(instance);
				}
				return;
			} else {
				// If you try to collect a instance to be recycled and the collection you are recyling it to is full, it is deleted instead.
				if (pCollection.length >= this.collectionLimit) {
					if (pCollected instanceof CollectorSingleton.DiobConstructor) {
						this.cleanInstance(pCollected);
						VYLO.delDiob(pCollected);
					} else {
						this.cleanInstance(pCollected);
						VYLO.delObject(pCollected);
					}
					return;
				}
				if (typeof(pCollected.onCollected) === 'function') pCollected.onCollected();
				if (!pCollection.includes(pCollected)) pCollection.push(pCollected);
				this.cleanInstance(pCollected);
				return;
			}
		} else {
			this.logger.prefix('Collector-Module').error('Collector: Invalid variable type passed for the %cpCollection', 'font-weight: bold', 'parameter. Expecting an array. Collect failed.');
		}
	}
	/**
	 * Gets diob instance(s) from the named collection and returns them. If no instances exist in the collection, a new one is created as a last resort.
	 * @param {string} pType - The diob type to find in the collection.
	 * @param {number} pNum - How many of the diob instances to get from the collection.
	 * @param {Array} pCollection - The collection to retrieve these instances from.
	 * @param  {...any} pRest - Remaining args to be passed into the new constructor of the diob or onDumped event
	 * @returns {Object} The diob instance that was in the collection.
	 */
	grab(pType='Diob', pNum=1, pCollection=[], ...pRest) {
		const reuseArray = [];
		let added = 0;
		let quantity = pNum;
		// Objects do not have a baseType variable
		let isObject = !VYLO.Type.getVariable(pType, 'baseType');
		if (!pCollection.length) {
			for (let i = 0; i < pNum; i++) {
				if (isObject) {
					reuseArray.push(VYLO.newObject(pType, ...pRest));
				} else {
					reuseArray.push(VYLO.newDiob(pType, ...pRest));
				}
			}
			if (reuseArray.length === 1) return reuseArray.pop();
			return reuseArray;
		} else {
			for (let j = pCollection.length - 1; j >= 0; j--) {
				if (quantity) {
					let instanceInCollection = pCollection[j];
					if (instanceInCollection.type === pType) {
						// Remove it from the collection
						pCollection.splice(j, 1);
						// Add it to the array that you will be getting from this collection
						reuseArray.push(instanceInCollection);
						// Label that this instance is no longer considered to be collection
						instanceInCollection.collectorCollected = false;
						// If this instance has a `onDumped` function defined call it.
						if (typeof(instanceInCollection.onDumped) === 'function') instanceInCollection.onDumped(...pRest);
						added++;
						quantity--;
					}
				}
			}
			// If the amount of instances we were supposed to get is greater than the instances we have gotten from the array, we need to generate more.
			if (pNum > added) {
				const missingQuantity = pNum - added;
				for (let x = 0; x < missingQuantity; x++) {
					if (isObject) {
						reuseArray.push(VYLO.newObject(pType, ...pRest));
					} else {
						reuseArray.push(VYLO.newDiob(pType, ...pRest));
					}
				}
			}

			if (reuseArray.length === 1) return reuseArray.pop();
			return reuseArray;
		}
	}
	/**
	 * Gets diob instance(s) from the named collection and returns them. If no instances exist in the collection, a new one is created as a last resort.
	 * @deprecated
	 * @param {string} pType - The diob type to find in the collection.
	 * @param {number} pNum - How many of the diob instances to get from the collection.
	 * @param {Array} pCollection - The collection to retrieve these instances from.
	 * @param  {...any} pRest - Remaining args to be passed into the new constructor of the diob or onDumped event
	 * @returns {Object} The diob instance that was in the collection.
	 */
	isInCollection = this.grab;
	/**
	 * Cleans the diob instance so it can be reused from a fresh state
	 * @private
	 * @param {Object} pDiob - The diob instance to clean.
	 */
	cleanInstance(pDiob) {
		if (pDiob) {
			if (pDiob instanceof CollectorSingleton.DiobConstructor) {
				const isInterface = (pDiob.baseType === 'Interface' || pDiob.type === 'Interface' || VYLO.Type.getInheritances(pDiob.type).includes('Interface'));
				if (pDiob.color) {
					if (typeof(pDiob.color) === 'object' && pDiob.color.constructor === Object) {
						pDiob.color.tint = 0xFFFFFF;
						pDiob.color = pDiob.color;
					} else {
						pDiob.color = null;
					}
				}
				if (typeof(pDiob.scale) === 'object') {
					pDiob.scale.x = pDiob.scale.y = 1;
					pDiob.scale = pDiob.scale;
				} else {
					pDiob.scale = 1;
				}
				if (isInterface) {
					pDiob.hide();
					if (VYLO.World.getCodeType() !== 'server') {
						VYLO.Client.removeInterfaceElement(pDiob.getInterfaceName(), pDiob, true);
					}
				}
				pDiob.playAnimation();
				pDiob.setTransition();
				pDiob.angle = 0;
				pDiob.alpha = 1;
				pDiob.xPos = 0;
				pDiob.yPos = 0;
				if (!isInterface) {
					pDiob.mapName = null;
					// PINGABLE
					pDiob.setLoc();
				}
				pDiob.text = '';
				pDiob.composite = '';
				if (pDiob.baseType === 'Movable') pDiob.move();
				for (const o of pDiob.getOverlays()) pDiob.removeOverlay(o.type);
				for (const fN of pDiob.getFilters()) pDiob.removeFilter(fN);
			}
			pDiob.collectorCollected = true;
			pDiob.inTicker = null;
			if (typeof(pDiob.clean) === 'function') pDiob.clean();
		}
	}
}
export const Collector = new CollectorSingleton();

import { Logger } from './vendor/logger.min.mjs';

/** 
 * @file A plugin that allows you to add event listeners on objects. Have your code called alongside an event!
 * 
 * @author https://github.com/doubleactii
 * @license Pulse is free software, available under the terms of a MIT style License.
 */
class PulseComponent {
	/**
	 * Array that tracks events for instances
	 * @private
	 * @type {Array}
	 */
	static tracker = { 'ids': [] };
	/**
	 * Array of stored IDs so that multiple of the same ID cannot be claimed
	 * @private
	 * @type {Array}
	 */
	static storedIDs = [];
	/**
	 * Weakmap to track data belonging to instances used in this module.
	 * @private
	 * @type {WeakMap}
	 */
	instanceWeakMap = new WeakMap();
	/**
	 * The version of the module.
	 */
	version = "VERSION_REPLACE_ME";

	constructor() {
		/**
		 * The logger module this module uses to log errors / logs
		 * @private
		 * @type {Object}
		 */
		this.logger = new Logger();
        this.logger.registerType('PulseComponent-Module', '#ff6600');
	}

	/**
	 * Generates a random ID based on the specified length and ensures its uniqueness
	 * within the stored IDs.
	 * @private
	 * @param {number} [pID = 7] - The length of the generated ID.
	 * @returns {string} A unique random ID.
	 */
	generateID(pID = 7) {
		const chars = 'ABCDEFGHIJKLMNOPQRSTUVWXYZabcdefghijklmnopqrstuvwxyz0123456789';
		const makeID = function() {
			let ID = '';
			for (let i = 0; i < pID; i++) {
				ID += chars.charAt(Math.floor(Math.random() * chars.length));
			}
			return ID;
		}
		let ID = makeID();
		while(PulseComponent.storedIDs.includes(ID)) {
			ID = makeID();
		}
		PulseComponent.storedIDs.push(ID);
		return ID;
	}
	/**
	 * Adds an event listener function to pInstance to call whenever pEventName is called
	 * 
	 * @param {Object} pInstance - The instance to add an event listener to
	 * @param {string} pEventName - The name of the event to add the listener to
	 * @param {Function} pFunction - The function to be called when this event is called
	 */
	on(pInstance, pEventName, pFunction) {
		if (!pInstance) {
			this.logger.prefix('PulseComponent-Module').error('pInstance not passed.');
			return;
		}
		if (!pEventName) {
			this.logger.prefix('PulseComponent-Module').error('pEventName not passed.');
			return;
		}
		if (typeof(pFunction) !== 'function') {
			this.logger.prefix('PulseComponent-Module').error('pFunction argument is missing or it is not of the function type!');
			return;
		}

		// Get the data stored for this instance
		let pulseComponentListenerID = this.instanceWeakMap.get(pInstance);
		// If it couldn't get any data stored, we need to generate an ID and store it
		if (!pulseComponentListenerID) {
			const ID = this.generateID();
			pulseComponentListenerID = ID;
			this.instanceWeakMap.set(pInstance, pulseComponentListenerID);
			PulseComponent.tracker.ids.push(pulseComponentListenerID);
			PulseComponent.tracker[pulseComponentListenerID] = {};
			PulseComponent.tracker[pulseComponentListenerID]['listened'] = {};
		}
		if (!PulseComponent.tracker[pulseComponentListenerID][pEventName]) PulseComponent.tracker[pulseComponentListenerID][pEventName] = { 'counter': 0 };
		const listenerID = ++PulseComponent.tracker[pulseComponentListenerID][pEventName].counter;
		if (!PulseComponent.tracker[pulseComponentListenerID]['listened'][pEventName]) this.listenForEvent(pInstance, pEventName);
		PulseComponent.tracker[pulseComponentListenerID][pEventName][listenerID] = pFunction;
		// If the event name is `onNew` then call immedietly, there is no way to capture this event
		if (pEventName === 'onNew') PulseComponent.tracker[pulseComponentListenerID][pEventName][listenerID].bind(pInstance)();
	};

	/**
	 * Removes an event listener function from pInstance
	 * 
	 * @param {Object} pInstance - The instance to remove the event listener from
	 * @param {string} pEventName - The name of the event to remove
	 * @param {Function} pFunction - The function to be removed
	 */
	off(pInstance, pEventName, pFunction) {
		if (!pInstance) {
			this.logger.prefix('PulseComponent-Module').error('pInstance not passed.');
			return;
		}
		if (!pEventName) {
			this.logger.prefix('PulseComponent-Module').error('pEventName not passed.');
			return;
		}
		if (typeof(pFunction) !== 'function') {
			this.logger.prefix('PulseComponent-Module').error('pFunction argument is missing or it is not of the function type!');
			return;
		}
		// Get the data stored for this instance
		const pulseComponentListenerID = this.instanceWeakMap.get(pInstance);
		// No event can be removed from this instance as its not even being tracked
		if (!pulseComponentListenerID) return;

		for (const listenerID in PulseComponent.tracker[pulseComponentListenerID][pEventName]) {
			if (PulseComponent.tracker[pulseComponentListenerID][pEventName][listenerID] === pFunction) {
				delete PulseComponent.tracker[pulseComponentListenerID][pEventName][listenerID];
				return;
			}
		}
		this.logger.prefix('PulseComponent-Module').error('This function is not being tracked.');
	};

	/**
	 * Listens for an event on an instance. Preserves any original event function code, while also allowing multiple event listeners to call on the same event.
	 * @private
	 * @param {Object} pInstance - The instance that has the event
	 * @param {string} pEventName - The event name to listen for
	 */
	listenForEvent(pInstance, pEventName) {
		// If there was a valid event for this type, or there was one already defined then we need to modify it to allow listening events
		let originalEvent = pInstance[pEventName];
		// Get the data stored for this instance
		const pulseComponentListenerID = this.instanceWeakMap.get(pInstance);
		const listener = function() {
			if (typeof(originalEvent) === 'function') {
				originalEvent.apply(pInstance, arguments);
			}
			// Loop the event name in the tracker to see if multiple events have been registered, if so we need to call each event when this event is dispatched.
			for (const listener in PulseComponent.tracker[pulseComponentListenerID][pEventName]) {
				if (typeof(PulseComponent.tracker[pulseComponentListenerID][pEventName][listener]) === 'function') {
					PulseComponent.tracker[pulseComponentListenerID][pEventName][listener].apply(pInstance, arguments);
				}
			}
		}
		pInstance[pEventName] = listener;
		PulseComponent.tracker[pulseComponentListenerID]['listened'][pEventName] = true;
	};
}

export const Pulse = new PulseComponent();

import { Tween } from "./vendor/tween.min.mjs";
import { Sound } from "./sound.mjs";
import { Logger } from './vendor/logger.min.mjs';

/**
* Class for managing sounds
* @class ResonanceSingleton  
* @license Resonance does not have a license at this time. For licensing contact the author
* @author https://github.com/doubleactii

Safari does not support .ogg files, so if you are using safari, do not use .ogg files or this library will not play that sound on safari.
Otherwise, you can use .ogg on any other platform. Cordova included. This supports mp3, wav, ogg, etc.
*/
class ResonanceSingleton {
	/**
	 * The max volume to be used
	 * 
	 * @type {number}
	 */
	static MAX_VOLUME = 200;
	/**
	 * The min volume to be used
	 * 
	 * @type {number}
	 */
	static MIN_VOLUME = -200;
	/**
	 * The max number of sounds that can be recycled
	 * 
	 * @type {number}
	 */
	static MAX_RECYCLED_SOUNDS = 500;
	/**
	 * The max speed this sound can be played at
	 * 
	 * @type {number}
	 */
	static MAX_PLAYBACK_RATE = 10;
	/**
	 * The frame rate at which to fade the sounds
	 * 
	 * @type {number}
	 */
	static FRAME_RATE = 60;
	/**
	 * 
	 * @param {number} pVal - Value to clamp
	 * @param {number} pMin - Minimum value
	 * @param {number} pMax - Maximum value
	 * @returns The clamped number
	 */
	static clamp = (pVal, pMin, pMax) => {
		return Math.max(pMin, Math.min(pVal, pMax));
	}
	/**
	 * 
	 * @param {number} pValue - The number to normalize within a range
	 * @param {number} pMin - The min number
	 * @param {number} pMax - The max number
	 * @param {number} pRange - The range to normalize within
	 * @returns The normalized number
	 */
	static normalize = (pValue, pMin=0, pMax=100, pRange=1) => {
		return (pValue - pMin) / (pMax - pMin) * pRange;
	}
	/**
	 * The version of the module.
	 */
	version = "VERSION_REPLACE_ME";
	constructor() {
		if (!window.AudioContext && !window.webkitAudioContext) {
			this.logger.prefix('Resonance-Module').error('Your device does not support window.AudioContext || window.webkitAudioContext. This library cannot be used');
			return;
		}

        /** The logger module this module uses to log errors / logs.
         * @private
         * @type {Object}
         */
        this.logger = new Logger();
        this.logger.registerType('Resonance-Module', '#ff6600');

		// For WebKit- and Blink-based browsers
		window.AudioContext = window.AudioContext || window.webkitAudioContext;
		// Attach events to start audio through gestures
		window.addEventListener('mousedown', this.resumeAudioCtx.bind(this));
		window.addEventListener('touchstart', this.resumeAudioCtx.bind(this));
		window.addEventListener('load', this.resumeAudioCtx.bind(this));
		// When the window is unfocused, all sounds are paused
		window.addEventListener('blur', () => {
			this.focused = false;
			if (this.soundsPlaying.length) this.pauseAllSounds(true);
		});
		// When the window is focused all sounds are resumed, and queued sounds are played
		window.addEventListener('focus', () => {
			this.focused = true;
			if (this.pausedSounds.length) this.resumeAllSounds(true);
			if (this.queuedSoundsToPlay.length || this.queuedSoundsToFade.length) this.playQueuedSounds();				
		});
		/**
		 * The audio context all audio will dervie from
		 * 
		 * @type {AudioContext}
		 */
		this.audioCtx = new AudioContext();
		// For older browser support that has this old API.
