		// 45 degrees: 'northeast'
		// 90 degrees: 'north'
		// 135 degrees: 'northwest'
		// 180 degrees: 'west'
		// 225 degrees: 'southwest'
		// 270 degrees: 'south'
		// 315 degrees: 'southeast'
