			hex = this.rgbToHex(pSwitch, pG, pB);
			rgb = this.hexToRgb(hex);
		} else {
			// Convert decimal to hex
			if (typeof(pSwitch) === 'number') {
				pSwitch = this.decimalToHex(pSwitch);
			}
			hex = pSwitch;
			// Convert hex to rgb
			rgb = this.hexToRgb(hex);
